import React, { Component } from 'react';
import image1 from "../../../assets/default.png"

class Slide extends Component {
    
    
    setActiveFrames(numberOfFrames){
        const showableFrames=numberOfFrames;
        let activeFrameString= "index === this.props.activeIndex ? \"Active\" : \"Inactive\"";
        for(let i=1; i>showableFrames; i++ ){
            activeFrameString = activeFrameString +  " \&\& index === this.props.activeIndex+" + showableFrames +" ? \"Active\" : \"Inactive\"";
        }
    }
    render() {
        return (
            <section>
                {
                    this.props.elements.map((s, index) => {
                        return (

                            <div className={this.setActiveFrames(this.props.numberOfFrames)} key={index} style={{ backgroundImage: `url(${s})` }}>
                                <img className="Image" src={s} alt={image1}></img>
                                
                            </div>

                        )
                    }
                    )}
            </section>
        )
    }
}
export default Slide;
