import React, { Component } from 'react';
import Slide from '../SliderControls/Slide';
import LeftPointer from '../SliderControls/LeftPointer';
import RightPointer from '../SliderControls/RightPointer';
import classes from './Slider.module.css';



class Slider extends Component {
    constructor(props) {
        super(props);

        this.state = {
            activeIndex: 0,
            length: ''
        };
    }

    componentWillMount() {
        this.setState({
            length: this.props.elements.length
        })

    }

    changePrevSlide() {
        let index = this.state.activeIndex;
        let length = this.state.length;
        if (index < 1) {
            index = length - 1;
        }
        else {
            index--;
        }
        this.setState({
            activeIndex: index
        });
    }
    changeNextSlide() {
        let length = this.state.length;
        let index = this.state.activeIndex;


        if (index === length - 1) {
            index = 0
        }
        else {
            index++;
        }
        this.setState({
            activeIndex: index
        });
    }

    render() {
        console.log(this.props.elements);
        console.log("Slide length is " + this.state.length)
        console.log("Currently active index" + this.state.activeIndex)
        return (

            <div className='slider'>

                <div className='slider-items'>
                    <table className={classes.Table}>
                        <tr className={classes.TrSlide}>
                            <td className={classes.LeftArrowTd} >
                                <LeftPointer
                                    changePrevSlide={() => this.changePrevSlide()}
                                />
                            </td>
                            <td className={classes.ContentTd}>
                                <div className='slider-text'>
                                    <Slide
                                        activeIndex={this.state.activeIndex}
                                        elements={this.props.elements}
                                    />
                                </div>
                            </td>

                            <td className={classes.RightArrowTd} >
                                <RightPointer
                                    changeNextSlide={() => this.changeNextSlide()}
                                />
                            </td>
                        </tr>
                    </table>

                </div>

            </div>
        );
    }



}

export default Slider;
