import React, { Component } from 'react';
import image1 from "../../../assets/default.png"
import { act } from 'react-test-renderer';

class Slide extends Component {
    constructor(props) {
        super(props);
        this.state = {
            activeFrameStrings: this.setActiveFrames(this.props.showableSlides),
        }
        
    }
    
    setActiveFrames(numberOfFrames){
        const showableFrames=numberOfFrames;
        console.log("Showing number of frames: " + showableFrames);
        var activeFrameString= "index === this.props.activeIndex ? \"Active\" : \"Inactive\"";
        let i=1
        console.log("Ennen looppia " + activeFrameString);
        for(i; i<showableFrames; i++ ){
            activeFrameString+= ` && index === this.props.activeIndex+${i} ? "Active" : "Inactive"` ;
            console.log("luupissa käyty");
        }
        console.log(activeFrameString);
        return activeFrameString;
    }
    render() {
        return (
            <section>
                {
                    this.props.elements.map((s, index) => {
                        return (

                            <div className={this.activeFrameStrings} key={index} style={{ backgroundImage: `url(${s})` }}>
                                <img className="Image" src={s} alt={image1}></img>
                                {this.props.showableFrames}
                            </div>

                        )
                    }
                    )}
            </section>
        )
    }
}
export default Slide;
