import React, { useContext } from 'react';
import PropTypes from 'prop-types';

import { withStyles } from 'direflow-component';
import styles from './App.css';
import Slider from './Slider/Slider';
import image1 from '../../assets/cat.jpg';
import image2 from '../../assets/dog.jpg';

const App = (props) => {

  return (
    <>

        <Slider elements={[populateElements(props)]}/>

    </>
  );
};

function populateElements(props) {
  const elementsToIterate = [props.image1, props.image2];
  const finalElements = [];
  elementsToIterate.map((e,index) =>{
    if (e != "empty"){
      finalElements.push(e);
    }
  })
  return finalElements;
}

App.defaultProps = {
  image1: "empty",
  image2: "empty",
}


App.propTypes = {
  image1: PropTypes.string,
  image2: PropTypes.string,
  componentTitle: PropTypes.string,
  sampleList: PropTypes.array,
};

export default withStyles(styles)(App);
